import React, { useState } from 'react';
import axios from 'axios';
import Sidebar from '../components/Sidebar'; // Adjust the path if your Sidebar is elsewhere

const ChangePassword = () => {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setMessage("");
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('You must be logged in to change your password.');
      return;
    }
    try {
      const res = await axios.post(
        'http://localhost:5000/api/auth/change-password',
        { oldPassword, newPassword },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage(res.data.message || 'Password changed successfully');
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setMessage('Session expired or unauthorized. Please log in again.');
      } else {
        setMessage(error.response?.data?.message || 'Something went wrong');
      }
    }
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh', background: '#f7fafd' }}>
      <Sidebar />
      <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <div className="change-password-box">
          <h2>
            <span role="img" aria-label="lock">ðŸ”’</span> Change Password
          </h2>
          <form onSubmit={handleChangePassword}>
            <label htmlFor="oldPassword">Old Password:</label>
            <input
              id="oldPassword"
              type="password"
              placeholder="Old Password"
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
              required
            />
            <label htmlFor="newPassword">New Password:</label>
            <input
              id="newPassword"
              type="password"
              placeholder="New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <button type="submit">Change Password</button>
            <p className={`change-password-message ${message.includes('success') ? 'success' : 'error'}`}>{message}</p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ChangePassword;
