// File: src/pages/ChangePassword.js
import React, { useState } from 'react';
import axios from 'axios';

const ChangePassword = () => {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setMessage("");
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('You must be logged in to change your password.');
      return;
    }
    try {
      const res = await axios.post(
        'http://localhost:5000/api/auth/change-password',
        { oldPassword, newPassword },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage(res.data.message || 'Password changed successfully');
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setMessage('Session expired or unauthorized. Please log in again.');
      } else {
        setMessage(error.response?.data?.message || 'Something went wrong');
      }
    }
  };

  return (
    <div style={{ padding: '40px', maxWidth: '400px', margin: 'auto' }}>
      <h2>Change Password</h2>
      <form onSubmit={handleChangePassword}>
        <input
          type="password"
          placeholder="Old Password"
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
          required
        />
        <br /><br />
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          required
        />
        <br /><br />
        <button type="submit">Change Password</button>
        <p style={{ color: message.includes('success') ? 'green' : 'red' }}>{message}</p>
      </form>
    </div>
  );
};

export default ChangePassword;
