import React, { useState } from 'react';
import axios from 'axios';

const ChangePassword = () => {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setMessage("");
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('You must be logged in to change your password.');
      return;
    }
    try {
      const res = await axios.post(
        'http://localhost:5000/api/auth/change-password',
        { oldPassword, newPassword },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage(res.data.message || 'Password changed successfully');
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setMessage('Session expired or unauthorized. Please log in again.');
      } else {
        setMessage(error.response?.data?.message || 'Something went wrong');
      }
    }
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '90vh',
    backgroundColor: '#f5f7fa',
    padding: '20px',
  };

  const boxStyle = {
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
    padding: '30px 40px',
    width: '100%',
    maxWidth: '400px',
  };

  const headingStyle = {
    textAlign: 'center',
    marginBottom: '25px',
    fontSize: '1.6rem',
    color: '#333',
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  };

  const labelStyle = {
    fontWeight: '500',
    color: '#444',
  };

  const inputStyle = {
    padding: '10px',
    borderRadius: '6px',
    border: '1px solid #ccc',
    outline: 'none',
    transition: 'border-color 0.3s',
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '6px',
    padding: '12px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  };

  const messageStyle = {
    marginTop: '10px',
    textAlign: 'center',
    fontWeight: '500',
    color: message.toLowerCase().includes('success') ? 'green' : 'red',
  };

  return (
    <div style={containerStyle}>
      <div style={boxStyle}>
        <h2 style={headingStyle}>ðŸ”’ Change Password</h2>
        <form onSubmit={handleChangePassword} style={formStyle}>
          <label htmlFor="oldPassword" style={labelStyle}>Old Password</label>
          <input
            id="oldPassword"
            type="password"
            placeholder="Enter your old password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            required
            style={inputStyle}
          />

          <label htmlFor="newPassword" style={labelStyle}>New Password</label>
          <input
            id="newPassword"
            type="password"
            placeholder="Enter your new password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
            style={inputStyle}
          />

          <button type="submit" style={buttonStyle}>Change Password</button>

          {message && <p style={messageStyle}>{message}</p>}
        </form>
      </div>
    </div>
  );
};

export default ChangePassword;
