import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    role: '',
    picture: ''
  });

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setProfile(res.data);
      } catch {
        // fallback: get from localStorage if backend fails
        setProfile({
          name: localStorage.getItem('name') || '',
          email: localStorage.getItem('email') || '',
          role: localStorage.getItem('role') || '',
          picture: ''
        });
      }
    };
    fetchProfile();
  }, []);

  return (
    <div style={{
      maxWidth: 420,
      margin: '40px auto',
      background: '#fff',
      borderRadius: 16,
      boxShadow: '0 4px 24px rgba(30,64,175,0.10)',
      padding: '40px 32px',
      minHeight: 400,
      textAlign: 'center'
    }}>
      <div style={{ marginBottom: 24 }}>
        <img
          src={profile.picture || '/default-profile.png'}
          alt="Profile"
          style={{
            width: 110,
            height: 110,
            borderRadius: '50%',
            objectFit: 'cover',
            border: '3px solid #007bff',
            marginBottom: 12
          }}
        />
      </div>
      <h2 style={{ marginBottom: 12 }}>{profile.name}</h2>
      <p style={{ color: '#555', marginBottom: 8 }}><strong>Email:</strong> {profile.email}</p>
      <p style={{ color: '#555', marginBottom: 8 }}><strong>Role:</strong> {profile.role}</p>
    </div>
  );
};

export default ProfilePage;
