import React, { useEffect, useState } from 'react';
import { useLocation, Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [profilePic, setProfilePic] = useState('');

  // Determine dashboard path based on user role
  const role = localStorage.getItem('role');
  let dashboardPath = '/';
  if (role === 'SuperAdmin') dashboardPath = '/super-admin';
  else if (role === 'Admin') dashboardPath = '/admin';
  else if (role === 'Employee') dashboardPath = '/employee';

  // Show logout only on dashboard pages
  const showLogout =
    location.pathname.startsWith('/super-admin') ||
    location.pathname.startsWith('/admin') ||
    location.pathname.startsWith('/employee');

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/login';
  };

  useEffect(() => {
    // Fetch profile picture from backend or localStorage
    const fetchPic = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await fetch('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        setProfilePic(data.picture || '/default-profile.png');
      } catch {
        setProfilePic('/default-profile.png');
      }
    };
    fetchPic();
  }, []);

  return (
    <nav style={{
      width: '100%',
      background: 'linear-gradient(90deg,rgb(0, 0, 0) 60%,rgb(0, 0, 0) 100%)',
      color: '#fff',
      padding: '0 16px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      height: '72px',
      boxShadow: '0 2px 12px rgba(25, 118, 210, 0.08)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      letterSpacing: '0.5px',
      minWidth: 0,
      overflow: 'hidden',
      boxSizing: 'border-box'
    }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: '12px', minWidth: 0 }}>
        <Link to={dashboardPath} style={{ textDecoration: 'none', color: '#fff' }}>
          <span style={{
            fontSize: '1.4rem',
            fontWeight: 700,
            letterSpacing: '1px',
            textShadow: '0 2px 8px rgba(0,0,0,0.08)',
            whiteSpace: 'nowrap',
            cursor: 'pointer'
          }}>
            INCOR GROUP
          </span>
        </Link>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '18px' }}>
        {showLogout && (
          <button
            onClick={handleLogout}
            style={{
              background: 'linear-gradient(90deg, #e74c3c 60%, #c0392b 100%)',
              border: 'none',
              color: '#fff',
              padding: '8px 18px',
              borderRadius: '14px',
              fontWeight: 600,
              fontSize: '1.1rem',
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(231,76,60,0.10)',
              transition: 'background 0.18s',
              whiteSpace: 'nowrap'
            }}
            onMouseOver={e => e.currentTarget.style.background = '#c0392b'}
            onMouseOut={e => e.currentTarget.style.background = 'linear-gradient(90deg, #e74c3c 60%, #c0392b 100%)'}
          >
            Logout
          </button>
        )}
        <img
          src={profilePic}
          alt="Profile"
          style={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            objectFit: 'cover',
            cursor: 'pointer',
            border: '2px solid #007bff'
          }}
          onClick={() => navigate('/profile')}
        />
      </div>
    </nav>
  );
};

export default Navbar;
