import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const [profilePic, setProfilePic] = useState('/default-profile.png');
  const token = localStorage.getItem('token');

  // Function to fetch profile picture
  const fetchProfilePic = async () => {
    if (!token) return;
    try {
      const res = await fetch('http://localhost:5000/api/auth/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setProfilePic(data.picture ? `http://localhost:5000${data.picture}` : '/default-profile.png');
    } catch {
      setProfilePic('/default-profile.png');
    }
  };

  useEffect(() => {
    fetchProfilePic();
  }, [token]);

  // Listen for profile updates (you can trigger this from ProfilePage)
  useEffect(() => {
    const handleProfileUpdate = () => {
      fetchProfilePic();
    };

    window.addEventListener('profileUpdated', handleProfileUpdate);
    return () => {
      window.removeEventListener('profileUpdated', handleProfileUpdate);
    };
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <nav style={{
      width: '100%',
      background: 'linear-gradient(90deg,rgb(0, 0, 0) 60%,rgb(0, 0, 0) 100%)',
      color: '#fff',
      padding: '0 16px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      height: '72px',
      boxShadow: '0 2px 12px rgba(25, 118, 210, 0.08)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
    }}>
      <div style={{
        fontSize: '1.6rem',
        fontWeight: 'bold',
        color: '#fff',
        letterSpacing: '1px'
      }}>
        INCOR GROUP
      </div>
      
      <div style={{ display: 'flex', alignItems: 'center', gap: '18px' }}>
        {token && (
          <button
            onClick={handleLogout}
            style={{
              background: 'linear-gradient(90deg, #e74c3c 60%, #c0392b 100%)',
              border: 'none',
              color: '#fff',
              padding: '8px 18px',
              borderRadius: '14px',
              fontWeight: 600,
              fontSize: '1.1rem',
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(231,76,60,0.10)',
              transition: 'background 0.18s',
              whiteSpace: 'nowrap'
            }}
          >
            Logout
          </button>
        )}
        {token && (
          <img
            src={profilePic}
            alt="Profile"
            style={{
              width: 40,
              height: 40,
              borderRadius: '50%',
              objectFit: 'cover',
              cursor: 'pointer',
              border: '2px solid #007bff'
            }}
            onClick={() => navigate('/profile')}
          />
        )}
      </div>
    </nav>
  );
};

export default Navbar;
