import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import SuperAdminSidebar from '../superadmin/components/Sidebar';
import AdminSidebar from '../admin/components/Sidebar';
import EmployeeSidebar from '../employee/components/Sidebar';

const SettingsPage = () => {
  const [settings, setSettings] = useState({
    // Notification Settings
    emailNotifications: {
      announcements: true,
      bookings: true,
      tasks: true,
      polls: true,
      issues: true
    },
    inAppNotifications: {
      enabled: true,
      sound: false,
      desktop: true
    },
    
    // Privacy Settings
    profileVisibility: 'all', // 'all', 'colleagues', 'admins'
    bookingVisibility: true,
    contactVisibility: true,
    
    // Application Preferences
    theme: 'light', // 'light', 'dark'
    language: 'en',
    dateFormat: 'DD/MM/YYYY',
    timeFormat: '12',
    defaultBookingDuration: 60,
    
    // Calendar Settings
    defaultCalendarView: 'month',
    workingHours: {
      start: '09:00',
      end: '18:00'
    },
    
    // Booking Preferences
    defaultRoom: '',
    bookingReminder: 15, // minutes
    autoCancel: false
  });

  const role = localStorage.getItem('role');
  let Sidebar = null;
  if (role === 'SuperAdmin') Sidebar = SuperAdminSidebar;
  else if (role === 'Admin') Sidebar = AdminSidebar;
  else if (role === 'Employee') Sidebar = EmployeeSidebar;

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/auth/settings', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSettings({ ...settings, ...res.data });
    } catch (err) {
      console.log('Using default settings');
    }
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put('http://localhost:5000/api/auth/settings', settings, {
        headers: { Authorization: `Bearer ${token}` }
      });
      toast.success('Settings saved successfully!');
    } catch (err) {
      toast.error('Failed to save settings');
    }
  };

  const handleChange = (section, key, value) => {
    setSettings(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [key]: value
      }
    }));
  };

  const handleDirectChange = (key, value) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh', background: '#f7fafd' }}>
      {Sidebar && <Sidebar />}
      <div style={{
        flex: 1,
        padding: '40px',
        maxWidth: '800px',
        margin: '0 auto',
        marginLeft: role ? '280px' : 'auto'
      }}>
        <div style={{
          background: '#fff',
          borderRadius: '16px',
          boxShadow: '0 8px 30px rgba(0,0,0,0.1)',
          padding: '40px'
        }}>
          <h2 style={{ marginBottom: '30px', color: '#333', fontSize: '2rem', textAlign: 'center' }}>
            ⚙️ Settings
          </h2>

          {/* Notification Settings */}
          <div style={{ marginBottom: '40px' }}>
            <h3 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.4rem' }}>
              📧 Notification Settings
            </h3>
            
            <div style={{ marginBottom: '20px' }}>
              <h4 style={{ color: '#333', marginBottom: '12px' }}>Email Notifications</h4>
              {Object.keys(settings.emailNotifications).map(key => (
                <label key={key} style={{ 
                  display: 'block', 
                  marginBottom: '10px',
                  fontSize: '16px',
                  color: '#555'
                }}>
                  <input
                    type="checkbox"
                    checked={settings.emailNotifications[key]}
                    onChange={(e) => handleChange('emailNotifications', key, e.target.checked)}
                    style={{ marginRight: '10px', transform: 'scale(1.2)' }}
                  />
                  {key.charAt(0).toUpperCase() + key.slice(1)}
                </label>
              ))}
            </div>

            <div>
              <h4 style={{ color: '#333', marginBottom: '12px' }}>In-App Notifications</h4>
              <label style={{ 
                display: 'block', 
                marginBottom: '10px',
                fontSize: '16px',
                color: '#555'
              }}>
                <input
                  type="checkbox"
                  checked={settings.inAppNotifications.enabled}
                  onChange={(e) => handleChange('inAppNotifications', 'enabled', e.target.checked)}
                  style={{ marginRight: '10px', transform: 'scale(1.2)' }}
                />
                Enable notifications
              </label>
              <label style={{ 
                display: 'block', 
                marginBottom: '10px',
                fontSize: '16px',
                color: '#555'
              }}>
                <input
                  type="checkbox"
                  checked={settings.inAppNotifications.sound}
                  onChange={(e) => handleChange('inAppNotifications', 'sound', e.target.checked)}
                  style={{ marginRight: '10px', transform: 'scale(1.2)' }}
                />
                Sound notifications
              </label>
            </div>
          </div>

          {/* Privacy Settings */}
          <div style={{ marginBottom: '40px' }}>
            <h3 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.4rem' }}>
              🔒 Privacy Settings
            </h3>
            
            <div style={{ marginBottom: '15px' }}>
              <label style={{ 
                fontWeight: 'bold', 
                marginBottom: '8px', 
                display: 'block',
                color: '#333',
                fontSize: '16px'
              }}>
                Profile Visibility:
              </label>
              <select
                value={settings.profileVisibility}
                onChange={(e) => handleDirectChange('profileVisibility', e.target.value)}
                style={{ 
                  padding: '10px', 
                  borderRadius: '6px', 
                  border: '1px solid #ccc',
                  fontSize: '16px',
                  width: '200px'
                }}
              >
                <option value="all">Everyone</option>
                <option value="colleagues">Colleagues only</option>
                <option value="admins">Admins only</option>
              </select>
            </div>

            <label style={{ 
              display: 'block', 
              marginBottom: '10px',
              fontSize: '16px',
              color: '#555'
            }}>
              <input
                type="checkbox"
                checked={settings.bookingVisibility}
                onChange={(e) => handleDirectChange('bookingVisibility', e.target.checked)}
                style={{ marginRight: '10px', transform: 'scale(1.2)' }}
              />
              Show my bookings to others
            </label>
          </div>

          {/* Application Preferences */}
          <div style={{ marginBottom: '40px' }}>
            <h3 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.4rem' }}>
              🎨 Application Preferences
            </h3>
            
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
              gap: '20px' 
            }}>
              <div>
                <label style={{ 
                  fontWeight: 'bold', 
                  marginBottom: '8px', 
                  display: 'block',
                  color: '#333',
                  fontSize: '16px'
                }}>
                  Theme:
                </label>
                <select
                  value={settings.theme}
                  onChange={(e) => handleDirectChange('theme', e.target.value)}
                  style={{ 
                    padding: '10px', 
                    borderRadius: '6px', 
                    border: '1px solid #ccc', 
                    width: '100%',
                    fontSize: '16px'
                  }}
                >
                  <option value="light">Light</option>
                  <option value="dark">Dark</option>
                </select>
              </div>

              <div>
                <label style={{ 
                  fontWeight: 'bold', 
                  marginBottom: '8px', 
                  display: 'block',
                  color: '#333',
                  fontSize: '16px'
                }}>
                  Date Format:
                </label>
                <select
                  value={settings.dateFormat}
                  onChange={(e) => handleDirectChange('dateFormat', e.target.value)}
                  style={{ 
                    padding: '10px', 
                    borderRadius: '6px', 
                    border: '1px solid #ccc', 
                    width: '100%',
                    fontSize: '16px'
                  }}
                >
                  <option value="DD/MM/YYYY">DD/MM/YYYY</option>
                  <option value="MM/DD/YYYY">MM/DD/YYYY</option>
                  <option value="YYYY-MM-DD">YYYY-MM-DD</option>
                </select>
              </div>

              <div>
                <label style={{ 
                  fontWeight: 'bold', 
                  marginBottom: '8px', 
                  display: 'block',
                  color: '#333',
                  fontSize: '16px'
                }}>
                  Time Format:
                </label>
                <select
                  value={settings.timeFormat}
                  onChange={(e) => handleDirectChange('timeFormat', e.target.value)}
                  style={{ 
                    padding: '10px', 
                    borderRadius: '6px', 
                    border: '1px solid #ccc', 
                    width: '100%',
                    fontSize: '16px'
                  }}
                >
                  <option value="12">12-hour</option>
                  <option value="24">24-hour</option>
                </select>
              </div>
            </div>
          </div>

          {/* Booking Preferences */}
          <div style={{ marginBottom: '40px' }}>
            <h3 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.4rem' }}>
              📅 Booking Preferences
            </h3>
            
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
              gap: '20px' 
            }}>
              <div>
                <label style={{ 
                  fontWeight: 'bold', 
                  marginBottom: '8px', 
                  display: 'block',
                  color: '#333',
                  fontSize: '16px'
                }}>
                  Default Booking Duration:
                </label>
                <select
                  value={settings.defaultBookingDuration}
                  onChange={(e) => handleDirectChange('defaultBookingDuration', parseInt(e.target.value))}
                  style={{ 
                    padding: '10px', 
                    borderRadius: '6px', 
                    border: '1px solid #ccc', 
                    width: '100%',
                    fontSize: '16px'
                  }}
                >
                  <option value={30}>30 minutes</option>
                  <option value={60}>1 hour</option>
                  <option value={120}>2 hours</option>
                </select>
              </div>

              <div>
                <label style={{ 
                  fontWeight: 'bold', 
                  marginBottom: '8px', 
                  display: 'block',
                  color: '#333',
                  fontSize: '16px'
                }}>
                  Booking Reminder:
                </label>
                <select
                  value={settings.bookingReminder}
                  onChange={(e) => handleDirectChange('bookingReminder', parseInt(e.target.value))}
                  style={{ 
                    padding: '10px', 
                    borderRadius: '6px', 
                    border: '1px solid #ccc', 
                    width: '100%',
                    fontSize: '16px'
                  }}
                >
                  <option value={5}>5 minutes</option>
                  <option value={15}>15 minutes</option>
                  <option value={30}>30 minutes</option>
                  <option value={60}>1 hour</option>
                </select>
              </div>
            </div>
          </div>

          {/* Security Settings */}
          <div style={{ marginBottom: '40px' }}>
            <h3 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.4rem' }}>
              🔐 Security Settings
            </h3>
            
            <button
              onClick={() => window.location.href = '/change-password'}
              style={{
                padding: '12px 24px',
                background: '#007bff',
                color: '#fff',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '16px',
                fontWeight: 'bold'
              }}
            >
              Change Password
            </button>
          </div>

          {/* Save Button */}
          <div style={{ textAlign: 'center', marginTop: '40px' }}>
            <button
              onClick={handleSave}
              style={{
                padding: '14px 40px',
                background: '#28a745',
                color: '#fff',
                border: 'none',
                borderRadius: '8px',
                fontSize: '18px',
                fontWeight: 'bold',
                cursor: 'pointer',
                boxShadow: '0 4px 12px rgba(40, 167, 69, 0.3)'
              }}
            >
              Save Settings
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPage;
